  return $scope.isCustomOpeningTime && !timeLabel ? '' : isOpen && !timeLabel ? "Opening time " + $scope.RestaurtantTime.openTime  + " to " + $scope.RestaurtantTime.closeTime : timeLabel ? timeLabel : '';
